dunes-script-module.script:
var createIssueBody = createIssueTmpl.getContentAsMimeAttachment().content;
createIssueBody = createIssueBody.replace('{{summary}}', summary)
createIssueBody = createIssueBody.replace('{{description}}', description)
createIssueBody = createIssueBody.replace('{{projectId}}', projectId)
createIssueBody = createIssueBody.replace('{{reporter}}', reporter)
createIssueBody = createIssueBody.replace('{{issueType}}', issueType)
createIssueBody = createIssueBody.replace('{{label}}', label)

System.log(createIssueBody)

var request = jiraHost.createRequest("POST", "/rest/api/2/issue", createIssueBody);
request.setHeader('content-type' , 'application/json');
if (jiraHost.authentication.type == "OAuth 2.0") {
	request.setHeader('Cookie' , 'auth-openid='+jiraHost.authentication.getRawAuthProperty(0))
}
System.log("request: " + request.fullUrl);
var response = request.execute();
System.log("statusCode: " + response.statusCode);
System.log("response: " + response.contentAsString);
result = new Properties();
result.put("statusCode", response.statusCode);
result.put("contentLength", response.contentLength);
result.put("headers", response.getAllHeaders());
result.put("contentAsString", response.contentAsString);

return result;
