dunes-script-module.script:
var request = jiraHost.createRequest("GET", "/rest/api/2/issue/" + jiraId, null);
System.log("request: " + request.fullUrl);
if (jiraHost.authentication.type == "OAuth 2.0") {
	request.setHeader('Cookie' , 'auth-openid='+jiraHost.authentication.getRawAuthProperty(0))
	}
var response = request.execute();
System.log("statusCode: " + response.statusCode);
System.debug("response: " + response.contentAsString);
result = new Properties();
result.put("statusCode", response.statusCode);
result.put("contentLength", response.contentLength);
result.put("headers", response.getAllHeaders());
result.put("contentAsString", response.contentAsString);

return result
